  return (
    <PageContainer
      title="知识文库"
      subTitle="项目文档管理与查询"
      extra={[
        <Button key="upload" type="primary">
          上传文档
        </Button>,
      ]}
    >
      {/* 统计卡片 */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col span={6}>
          <Card>
            <Statistic
              title="文档总数"
              value={stats.total || 0}
              prefix={<FileTextOutlined />}
            />
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Statistic
              title="需求报告"
              value={stats.requirement || 0}
              valueStyle={{ color: '#1890ff' }}
            />
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Statistic
              title="验收报告"
              value={stats.acceptance || 0}
              valueStyle={{ color: '#52c41a' }}
            />
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Statistic
              title="设计文档"
              value={stats.design || 0}
              valueStyle={{ color: '#faad14' }}
            />
          </Card>
        </Col>
      </Row>

      {/* 搜索和筛选 */}
      <Card style={{ marginBottom: 16 }}>
        <Space size="large" style={{ width: '100%' }}>
          <Search
            placeholder="搜索文档标题或项目名称"
            allowClear
            enterButton={<SearchOutlined />}
            size="large"
            onSearch={handleSearch}
            style={{ width: 400 }}
          />
          <Select
            value={filterType}
            onChange={setFilterType}
            style={{ width: 200 }}
            size="large"
          >
            <Option value="all">全部类型</Option>
            <Option value="requirement">需求报告</Option>
            <Option value="acceptance">验收报告</Option>
            <Option value="design">设计文档</Option>
            <Option value="other">其他</Option>
          </Select>
        </Space>
      </Card>

      {/* 文档列表 */}
      <Card>
        <List
          itemLayout="horizontal"
          dataSource={filteredDocuments}
          pagination={{
            pageSize: 10,
            showSizeChanger: true,
            showTotal: (total) => `共 ${total} 个文档`,
          }}
          renderItem={(item) => (
            <List.Item
              actions={[
                <Button
                  key="preview"
                  type="link"
                  icon={<EyeOutlined />}
                  onClick={() => handlePreview(item)}
                >
                  预览
                </Button>,
                <Button
                  key="download"
                  type="link"
                  icon={<DownloadOutlined />}
                  onClick={() => handleDownload(item)}
                >
                  下载
                </Button>,
              ]}
            >
              <List.Item.Meta
                avatar={getFileIcon(item.type)}
                title={
                  <Space>
                    {item.title}
                    {getTypeTag(item.type)}
                  </Space>
                }
                description={
                  <div>
                    <p>项目: {item.project}</p>
                    <Space>
                      <span>上传时间: {item.uploadDate}</span>
                      <span>大小: {item.size}</span>
                    </Space>
                    <div style={{ marginTop: 8 }}>
                      {item.tags?.map((tag, index) => (
                        <Tag key={index}>{tag}</Tag>
                      ))}
                    </div>
                  </div>
                }
              />
            </List.Item>
          )}
        />
      </Card>

      {/* 预览弹窗 */}
      <Modal
        title={currentDoc?.title}
        open={previewVisible}
        onCancel={() => setPreviewVisible(false)}
        width={800}
        footer={[
          <Button key="download" type="primary" icon={<DownloadOutlined />} onClick={() => currentDoc && handleDownload(currentDoc)}>
            下载
          </Button>,
          <Button key="close" onClick={() => setPreviewVisible(false)}>
            关闭
          </Button>,
        ]}
      >
        {currentDoc && (
          <div style={{ padding: 24, background: '#f5f5f5', minHeight: 400 }}>
            <p style={{ textAlign: 'center', color: '#999' }}>
              文档预览功能开发中...
            </p>
            <p style={{ textAlign: 'center', color: '#999' }}>
              文档: {currentDoc.title}
            </p>
          </div>
        )}
      </Modal>
    </PageContainer>
  );
};

export default Knowledge;

