const LoginMessage: React.FC<{
  content: string;
}> = ({ content }) => {
  return (
    <Alert
      style={{
        marginBottom: 24,
      }}
      message={content}
      type="error"
      showIcon
    />
  );
};

// 
const AnimatedText: React.FC<{ text: string; interval?: number }> = ({
  text,
  interval = 30
}) => {
  const chars = text.split('');

  return (
    <>
      <style>
        {`
          @keyframes charFadeInAnim {
            0% {
              opacity: 0;
              transform: translateY(-20px) scale(0.8);
            }
            100% {
              opacity: 1;
              transform: translateY(0) scale(1);
            }
          }
        `}
      </style>
      {chars.map((char, index) => (
        <span
          key={`char-${index}`}
          style={{
            display: 'inline-block',
            opacity: 0,
            animation: 'charFadeInAnim 0.3s cubic-bezier(0.25, 0.46, 0.45, 0.94) forwards',
            animationDelay: `${index * interval}ms`,
          }}
        >
          {char === ' ' ? '\u00A0' : char}
        </span>
      ))}
    </>
  );
};

const Login: React.FC = () => {
  const [userLoginState, setUserLoginState] = useState<API.LoginResult>({});
  const [type, setType] = useState<string>("account");
  const { initialState, setInitialState } = useModel("@@initialState");
  const { styles } = useStyles();
  const { message } = App.useApp();
  const intl = useIntl();

  //  -  ref 
  const mousePositionRef = useRef({ x: 0.5, y: 0.5 });
  const ballRefs = useRef<(HTMLDivElement | null)[]>([]);

  //  - 
  useEffect(() => {
    const handleMouseMove = (e: MouseEvent) => {
      // 0  1
      const x = e.clientX / window.innerWidth;
      const y = e.clientY / window.innerHeight;
      mousePositionRef.current = { x, y };

      // -1  1
      const offsetX = (x - 0.5) * 2;
      const offsetY = (y - 0.5) * 2;

      // 
      ballRefs.current.forEach((ball, index) => {
        if (ball) {
          // 
          const speeds = [20, 15, 25, 18, 12];
          const speed = speeds[index] || 15;

          const moveX = offsetX * speed;
          const moveY = offsetY * speed;

          //  CSS 
          ball.style.setProperty('--mouse-x', `${moveX}px`);
          ball.style.setProperty('--mouse-y', `${moveY}px`);
        }
      });
    };

    window.addEventListener('mousemove', handleMouseMove);

    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  const fetchUserInfo = async () => {
    const userInfo = await initialState?.fetchUserInfo?.();
    if (userInfo) {
      flushSync(() => {
        setInitialState((s) => ({
          ...s,
          currentUser: userInfo,
        }));
      });
    }
  };

  const handleSubmit = async (values: API.LoginParams) => {
    try {
      //  Mock 
      if (values.username === 'admin' && values.password === 'admin') {
        const defaultLoginSuccessMessage = intl.formatMessage({
          id: "pages.login.success",
          defaultMessage: "",
        });
        message.success(defaultLoginSuccessMessage);

        //  localStorage
        localStorage.setItem('userToken', 'mock-token-' + Date.now());
        localStorage.setItem('currentUser', JSON.stringify({
          name: 'Admin',
          avatar: 'https://gw.alipayobjects.com/zos/antfincdn/XAosXuNZyF/BiazfanxmamNRoxxVxka.png',
          userid: '00000001',
          email: 'admin@example.com',
          signature: '',
          title: '',
          group: '',
          access: 'admin',
        }));

        // 
        setUserLoginState({
          status: 'ok',
          type,
          currentAuthority: 'admin',
        });

        // 
        flushSync(() => {
          setInitialState((s) => ({
            ...s,
            currentUser: {
              name: 'Admin',
              avatar: 'https://gw.alipayobjects.com/zos/antfincdn/XAosXuNZyF/BiazfanxmamNRoxxVxka.png',
              userid: '00000001',
              email: 'admin@example.com',
              signature: '',
              title: '',
              group: '',
              access: 'admin',
            },
          }));
        });

        // 
        const urlParams = new URL(window.location.href).searchParams;
        const redirect = urlParams.get("redirect") || "/dashboard";

        //  history.push 
        setTimeout(() => {
          history.push(redirect);
        }, 100);
        return;
      }

      // 
      const defaultLoginFailureMessage = intl.formatMessage({
        id: "pages.login.failure",
        defaultMessage: "",
      });
      message.error(defaultLoginFailureMessage);
      setUserLoginState({
        status: 'error',
        type,
        currentAuthority: 'guest',
      });
    } catch (error) {
      const defaultLoginFailureMessage = intl.formatMessage({
        id: "pages.login.failure",
        defaultMessage: "",
      });
      console.log(':', error);
      message.error(defaultLoginFailureMessage);
    }
  };
  const { status, type: loginType } = userLoginState;

  return (
    <div className={styles.container}>
      <Helmet>
        <title> - </title>
      </Helmet>
      <style>
        {`
          @keyframes float {
            0%, 100% {
              transform: translate(var(--mouse-x, 0px), var(--mouse-y, 0px)) translateY(0) translateX(0);
            }
            25% {
              transform: translate(var(--mouse-x, 0px), var(--mouse-y, 0px)) translateY(-20px) translateX(10px);
            }
            50% {
              transform: translate(var(--mouse-x, 0px), var(--mouse-y, 0px)) translateY(-10px) translateX(-10px);
            }
            75% {
              transform: translate(var(--mouse-x, 0px), var(--mouse-y, 0px)) translateY(-30px) translateX(5px);
            }
          }
        `}
      </style>

      {/*  */}
      <div
        ref={(el) => { ballRefs.current[0] = el; }}
        className={`${styles.floatingBall} ${styles.ball1}`}
      />
      <div
        ref={(el) => { ballRefs.current[1] = el; }}
        className={`${styles.floatingBall} ${styles.ball2}`}
      />
      <div
        ref={(el) => { ballRefs.current[2] = el; }}
        className={`${styles.floatingBall} ${styles.ball3}`}
      />
      <div
        ref={(el) => { ballRefs.current[3] = el; }}
        className={`${styles.floatingBall} ${styles.ball4}`}
      />
      <div
        ref={(el) => { ballRefs.current[4] = el; }}
        className={`${styles.floatingBall} ${styles.ball5}`}
      />

      <div className={styles.content}>
        <div className={styles.loginBox}>
          <div className={styles.header}>
            <div className={styles.logoWrapper}>
              <SlackOutlined style={{ fontSize: "36px", color: "#1890ff" }} />
              <h1 className={styles.title}>
                <AnimatedText text="OutBook Analysis System" interval={25} />
              </h1>
            </div>
            <div className={styles.subtitle}>
              OutBook - 
            </div>
          </div>

          {status === "error" && <LoginMessage content="" />}

          <LoginForm
            logo={false}
            title={false}
            subTitle={false}
            submitter={{
              searchConfig: {
                submitText: "",
              },
              resetButtonProps: {
                style: { display: "none" },
              },
              submitButtonProps: {
                size: "large",
                style: {
                  width: "100%",
                  height: "40px",
                  fontSize: "16px",
                },
              },
            }}
            onFinish={async (values) => {
              await handleSubmit(values as API.LoginParams);
            }}
          >
            <div className={styles.tabs}>
              <div style={{ marginBottom: "24px", textAlign: "center" }}>
                <span
                  style={{
                    color: "#1890ff",
                    fontSize: "16px",
                    fontWeight: 500,
                    borderBottom: "2px solid #1890ff",
                    paddingBottom: "8px",
                    cursor: "pointer",
                  }}
                >

                </span>
              </div>
            </div>

            <ProFormText
              name="username"
              fieldProps={{
                size: "large",
                prefix: <UserOutlined style={{ color: "#999" }} />,
                style: { width: "100%" },
              }}
              placeholder="admin"
              rules={[
                {
                  required: true,
                  message: "",
                },
              ]}
            />
            <ProFormText.Password
              name="password"
              fieldProps={{
                size: "large",
                prefix: <LockOutlined style={{ color: "#999" }} />,
                style: { width: "100%" },
              }}
              placeholder=""
              rules={[
                {
                  required: true,
                  message: "",
                },
              ]}
            />

            <div
              style={{
                marginBottom: "24px",
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center",
              }}
            >
              <label
                style={{
                  display: "flex",
                  alignItems: "center",
                  cursor: "pointer",
                }}
              >
                <input
                  type="checkbox"
                  defaultChecked
                  style={{ marginRight: "8px" }}
                />
                <span style={{ color: "#666" }}></span>
              </label>
            </div>
          </LoginForm>
        </div>
      </div>
    </div>
  );
};

export default Login;

