import { PageContainer, ProCard } from '@ant-design/pro-components';
import { Button, Card, DatePicker, Form, List, Space, Tag, message, Modal, Spin, Select } from 'antd';
import { FileTextOutlined, DownloadOutlined, RobotOutlined, CalendarOutlined } from '@ant-design/icons';
import { useEffect, useState } from 'react';
import { request } from '@umijs/max';
import moment from 'moment';

const { RangePicker } = DatePicker;

interface ReportType {
  id: string;
  title: string;
  type: 'monthly' | 'quarterly';
  period: string;
  generatedAt: string;
  summary: string;
}

const Reports: React.FC = () => {
  const [aiModalVisible, setAiModalVisible] = useState(false);
  const [aiGenerating, setAiGenerating] = useState(false);
  const [form] = Form.useForm();

  //  Mock 
  const monthlyReports: ReportType[] = [
    {
      id: '1',
      title: '20246',
      type: 'monthly',
      period: '2024-06',
      generatedAt: '2024-07-01 10:30:00',
      summary: '3746298.4BUG95%',
    },
    {
      id: '2',
      title: '20245',
      type: 'monthly',
      period: '2024-05',
      generatedAt: '2024-06-01 10:30:00',
      summary: '2678271.2215%',
    },
    {
      id: '3',
      title: '20244',
      type: 'monthly',
      period: '2024-04',
      generatedAt: '2024-05-01 10:30:00',
      summary: '4593237.298%',
    },
    {
      id: '4',
      title: '20243',
      type: 'monthly',
      period: '2024-03',
      generatedAt: '2024-04-01 10:30:00',
      summary: '3458183.22',
    },
  ];

  const quarterlyReports: ReportType[] = [
    {
      id: 'q1',
      title: '2024',
      type: 'quarterly',
      period: '2024-Q2',
      generatedAt: '2024-07-05 14:00:00',
      summary: '121977806.820%',
    },
    {
      id: 'q2',
      title: '2024',
      type: 'quarterly',
      period: '2024-Q1',
      generatedAt: '2024-04-05 14:00:00',
      summary: '81230492310%',
    },
  ];

  const fetchReports = async () => {
    // 
  };

  const handleGenerateReport = async (values: any) => {
    try {
      const response = await request('/api/reports/generate', {
        method: 'POST',
        data: values,
      });

      if (response.success) {
        message.success('');
        fetchReports();
      }
    } catch (error) {
      message.error('');
    }
  };

  const handleAIGenerate = async (values: any) => {
    setAiGenerating(true);
    try {
      //  AI 
      await new Promise(resolve => setTimeout(resolve, 2000));
      message.success('AI ');
      setAiModalVisible(false);
      fetchReports();
    } catch (error) {
      message.error('AI ');
    } finally {
      setAiGenerating(false);
    }
  };

  const handleDownload = (report: ReportType) => {
    message.info(`: ${report.title}`);
    // 
  };

  return (
    <PageContainer
      title=""
      subTitle=""
      extra={[
        <Button
          key="ai"
          type="primary"
          icon={<RobotOutlined />}
          onClick={() => setAiModalVisible(true)}
        >
          AI 
        </Button>,
      ]}
    >
      <ProCard split="vertical">
        <ProCard title="" colSpan="50%">
          <Form
            form={form}
            layout="inline"
            onFinish={handleGenerateReport}
            style={{ marginBottom: 16 }}
          >
            <Form.Item name="month" label="">
              <DatePicker picker="month" />
            </Form.Item>
            <Form.Item>
              <Button type="primary" htmlType="submit">

              </Button>
            </Form.Item>
          </Form>

          <List
            dataSource={monthlyReports}
            renderItem={(item) => (
              <List.Item
                actions={[
                  <Button
                    key="download"
                    type="link"
                    icon={<DownloadOutlined />}
                    onClick={() => handleDownload(item)}
                  >

                  </Button>,
                ]}
              >
                <List.Item.Meta
                  avatar={<FileTextOutlined style={{ fontSize: 24, color: '#1890ff' }} />}
                  title={
                    <Space>
                      {item.title}
                      <Tag color="blue"></Tag>
                    </Space>
                  }
                  description={
                    <div>
                      <p>{item.summary}</p>
                      <p style={{ color: '#999', fontSize: 12 }}>
                        <CalendarOutlined /> : {item.generatedAt}
                      </p>
                    </div>
                  }
                />
              </List.Item>
            )}
          />
        </ProCard>

        <ProCard title="" colSpan="50%">
          <Form
            layout="inline"
            onFinish={handleGenerateReport}
            style={{ marginBottom: 16 }}
          >
            <Form.Item name="quarter" label="">
              <DatePicker picker="quarter" />
            </Form.Item>
            <Form.Item>
              <Button type="primary" htmlType="submit">

              </Button>
            </Form.Item>
          </Form>

          <List
            dataSource={quarterlyReports}
            renderItem={(item) => (
              <List.Item
                actions={[
                  <Button
                    key="download"
                    type="link"
                    icon={<DownloadOutlined />}
                    onClick={() => handleDownload(item)}
                  >

                  </Button>,
                ]}
              >
                <List.Item.Meta
                  avatar={<FileTextOutlined style={{ fontSize: 24, color: '#52c41a' }} />}
                  title={
                    <Space>
                      {item.title}
                      <Tag color="green"></Tag>
                    </Space>
                  }
                  description={
                    <div>
                      <p>{item.summary}</p>
                      <p style={{ color: '#999', fontSize: 12 }}>
                        <CalendarOutlined /> : {item.generatedAt}
                      </p>
                    </div>
                  }
                />
              </List.Item>
            )}
          />
        </ProCard>
      </ProCard>

      {/* AI  */}
      <Modal
        title={
          <Space>
            <RobotOutlined style={{ color: '#1890ff' }} />
            AI 
          </Space>
        }
        open={aiModalVisible}
        onCancel={() => setAiModalVisible(false)}
        footer={null}
      >
        <Spin spinning={aiGenerating} tip="AI ...">
          <Form
            layout="vertical"
            onFinish={handleAIGenerate}
          >
            <Form.Item
              name="dateRange"
              label=""
              rules={[{ required: true, message: '' }]}
            >
              <RangePicker style={{ width: '100%' }} />
            </Form.Item>
            <Form.Item
              name="reportType"
              label=""
              rules={[{ required: true, message: '' }]}
            >
              <Select
                style={{ width: '100%' }}
                placeholder=""
                options={[
                  { label: '', value: 'monthly' },
                  { label: '', value: 'quarterly' },
                  { label: '', value: 'yearly' },
                ]}
              />
            </Form.Item>
            <Form.Item>
              <Space style={{ width: '100%', justifyContent: 'flex-end' }}>
                <Button onClick={() => setAiModalVisible(false)}>

                </Button>
                <Button type="primary" htmlType="submit" icon={<RobotOutlined />}>
                  AI 
                </Button>
              </Space>
            </Form.Item>
          </Form>
          <Card size="small" style={{ marginTop: 16, background: '#f0f2f5' }}>
            <p style={{ margin: 0, color: '#666' }}>
               AI 
            </p>
          </Card>
        </Spin>
      </Modal>
    </PageContainer>
  );
};

export default Reports;

