import { PageContainer } from '@ant-design/pro-components';
import { Card, List, Tag, Space, Button, Input, Select, Statistic, Row, Col, Modal } from 'antd';
import { FileTextOutlined, FilePdfOutlined, FileWordOutlined, EyeOutlined, DownloadOutlined, SearchOutlined } from '@ant-design/icons';
import { useEffect, useState } from 'react';
import { request } from '@umijs/max';

const { Search } = Input;
const { Option } = Select;

interface DocumentType {
  id: string;
  title: string;
  type: string;
  project: string;
  uploadDate: string;
  size: string;
  tags: string[];
}

const Knowledge: React.FC = () => {
  const [filterType, setFilterType] = useState<string>('all');
  const [previewVisible, setPreviewVisible] = useState(false);
  const [currentDoc, setCurrentDoc] = useState<DocumentType | null>(null);

  //  Mock 
  const mockDocuments: DocumentType[] = [
    {
      id: '1',
      title: '',
      type: 'requirement',
      project: '',
      uploadDate: '2024-01-10',
      size: '2.3 MB',
      tags: ['', '', ''],
    },
    {
      id: '2',
      title: '',
      type: 'acceptance',
      project: '',
      uploadDate: '2024-07-01',
      size: '1.8 MB',
      tags: ['', '', ''],
    },
    {
      id: '3',
      title: '',
      type: 'design',
      project: '',
      uploadDate: '2024-03-05',
      size: '4.5 MB',
      tags: ['', '', ''],
    },
    {
      id: '4',
      title: '',
      type: 'requirement',
      project: '',
      uploadDate: '2024-02-05',
      size: '3.1 MB',
      tags: ['', '', ''],
    },
    {
      id: '5',
      title: '',
      type: 'acceptance',
      project: '',
      uploadDate: '2024-07-25',
      size: '2.2 MB',
      tags: ['', '', ''],
    },
    {
      id: '6',
      title: '',
      type: 'design',
      project: '',
      uploadDate: '2024-04-10',
      size: '3.8 MB',
      tags: ['', '', ''],
    },
    {
      id: '7',
      title: '',
      type: 'requirement',
      project: '',
      uploadDate: '2024-04-25',
      size: '2.7 MB',
      tags: ['', '', ''],
    },
    {
      id: '8',
      title: '',
      type: 'design',
      project: '',
      uploadDate: '2024-05-28',
      size: '4.2 MB',
      tags: ['', '', ''],
    },
    {
      id: '9',
      title: '',
      type: 'requirement',
      project: '',
      uploadDate: '2024-06-25',
      size: '3.5 MB',
      tags: ['', '', ''],
    },
    {
      id: '10',
      title: '',
      type: 'other',
      project: '',
      uploadDate: '2024-01-05',
      size: '1.2 MB',
      tags: ['', '', ''],
    },
  ];

  const [documents] = useState<DocumentType[]>(mockDocuments);
  const [filteredDocuments, setFilteredDocuments] = useState<DocumentType[]>(mockDocuments);

  const stats = {
    total: mockDocuments.length,
    requirement: mockDocuments.filter(d => d.type === 'requirement').length,
    acceptance: mockDocuments.filter(d => d.type === 'acceptance').length,
    design: mockDocuments.filter(d => d.type === 'design').length,
  };

  useEffect(() => {
    filterDocuments();
  }, [filterType]);

  const fetchData = async () => {
    // 
  };

  const filterDocuments = () => {
    if (filterType === 'all') {
      setFilteredDocuments(documents);
    } else {
      setFilteredDocuments(documents.filter(doc => doc.type === filterType));
    }
  };

  const handleSearch = (value: string) => {
    if (!value) {
      filterDocuments();
      return;
    }
    const filtered = documents.filter(doc =>
      doc.title.toLowerCase().includes(value.toLowerCase()) ||
      doc.project.toLowerCase().includes(value.toLowerCase())
    );
    setFilteredDocuments(filtered);
  };

  const getFileIcon = (type: string) => {
    switch (type) {
      case 'requirement':
        return <FileTextOutlined style={{ fontSize: 24, color: '#1890ff' }} />;
      case 'acceptance':
        return <FilePdfOutlined style={{ fontSize: 24, color: '#52c41a' }} />;
      case 'design':
        return <FileWordOutlined style={{ fontSize: 24, color: '#faad14' }} />;
      default:
        return <FileTextOutlined style={{ fontSize: 24, color: '#999' }} />;
    }
  };

  const getTypeTag = (type: string) => {
    const typeMap: Record<string, { color: string; text: string }> = {
      requirement: { color: 'blue', text: '' },
      acceptance: { color: 'green', text: '' },
      design: { color: 'orange', text: '' },
      other: { color: 'default', text: '' },
    };
    const config = typeMap[type] || typeMap.other;
    return <Tag color={config.color}>{config.text}</Tag>;
  };

  const handlePreview = (doc: DocumentType) => {
    setCurrentDoc(doc);
    setPreviewVisible(true);
  };

  const handleDownload = (doc: DocumentType) => {
    console.log(':', doc.title);
    // 
  };

  return (
    <PageContainer
      title=""
      subTitle=""
      extra={[
        <Button key="upload" type="primary">

        </Button>,
      ]}
    >
      {/*  */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col span={6}>
          <Card>
            <Statistic
              title=""
              value={stats.total || 0}
              prefix={<FileTextOutlined />}
            />
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Statistic
              title=""
              value={stats.requirement || 0}
              valueStyle={{ color: '#1890ff' }}
            />
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Statistic
              title=""
              value={stats.acceptance || 0}
              valueStyle={{ color: '#52c41a' }}
            />
          </Card>
        </Col>
        <Col span={6}>
          <Card>
            <Statistic
              title=""
              value={stats.design || 0}
              valueStyle={{ color: '#faad14' }}
            />
          </Card>
        </Col>
      </Row>

      {/*  */}
      <Card style={{ marginBottom: 16 }}>
        <Space size="large" style={{ width: '100%' }}>
          <Search
            placeholder=""
            allowClear
            enterButton={<SearchOutlined />}
            size="large"
            onSearch={handleSearch}
            style={{ width: 400 }}
          />
          <Select
            value={filterType}
            onChange={setFilterType}
            style={{ width: 200 }}
            size="large"
          >
            <Option value="all"></Option>
            <Option value="requirement"></Option>
            <Option value="acceptance"></Option>
            <Option value="design"></Option>
            <Option value="other"></Option>
          </Select>
        </Space>
      </Card>

      {/*  */}
      <Card>
        <List
          itemLayout="horizontal"
          dataSource={filteredDocuments}
          pagination={{
            pageSize: 10,
            showSizeChanger: true,
            showTotal: (total) => ` ${total} `,
          }}
          renderItem={(item) => (
            <List.Item
              actions={[
                <Button
                  key="preview"
                  type="link"
                  icon={<EyeOutlined />}
                  onClick={() => handlePreview(item)}
                >

                </Button>,
                <Button
                  key="download"
                  type="link"
                  icon={<DownloadOutlined />}
                  onClick={() => handleDownload(item)}
                >

                </Button>,
              ]}
            >
              <List.Item.Meta
                avatar={getFileIcon(item.type)}
                title={
                  <Space>
                    {item.title}
                    {getTypeTag(item.type)}
                  </Space>
                }
                description={
                  <div>
                    <p>: {item.project}</p>
                    <Space>
                      <span>: {item.uploadDate}</span>
                      <span>: {item.size}</span>
                    </Space>
                    <div style={{ marginTop: 8 }}>
                      {item.tags?.map((tag, index) => (
                        <Tag key={index}>{tag}</Tag>
                      ))}
                    </div>
                  </div>
                }
              />
            </List.Item>
          )}
        />
      </Card>

      {/*  */}
      <Modal
        title={currentDoc?.title}
        open={previewVisible}
        onCancel={() => setPreviewVisible(false)}
        width={800}
        footer={[
          <Button key="download" type="primary" icon={<DownloadOutlined />} onClick={() => currentDoc && handleDownload(currentDoc)}>

          </Button>,
          <Button key="close" onClick={() => setPreviewVisible(false)}>

          </Button>,
        ]}
      >
        {currentDoc && (
          <div style={{ padding: 24, background: '#f5f5f5', minHeight: 400 }}>
            <p style={{ textAlign: 'center', color: '#999' }}>
              ...
            </p>
            <p style={{ textAlign: 'center', color: '#999' }}>
              : {currentDoc.title}
            </p>
          </div>
        )}
      </Modal>
    </PageContainer>
  );
};

export default Knowledge;

